INSERT INTO `Stackoverflow_jobs_12_june` VALUES ('644','Working Student, Internship or Thesis in Berlin - Frontend, JavaScript, ReactJS at Zenjob (Berlin, Germany)','<p>For this position we are looking for someone who is passionate about frontend development with&nbsp;React. You will work on complex problems with cutting-edge technologies, in a tech-driven company.</p><br /><p><strong>If you join us, you&rsquo;ll:</strong></p><br /><ul><br /><li>learn to develop responsive, fun-to-use web applications for&nbsp;our B2B and B2C users</li><br /><li>have the option to write your thesis at Zenjob</li><br /><li>have the option to get a full-time contract when you graduate</li><br /><li>get a competitive salary</li><br /></ul>','Zenjob');
